controller libraryjimport('joomla.application.component.controller'); // Get an instance of the controller prefixed by OSBIT$controller = JController::getInstance('OSBIT'); // Perform the Request task$controller->execute(JRequest::getCmd('task')); // Redirect if set by the controller$controller->redirect();